import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useApp } from '../context/AppContext';
import './ProductDetail.css';

function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart, toggleCartSidebar } = useApp();
  const [quantity, setQuantity] = useState(1);
  const [selectedSize, setSelectedSize] = useState('M');

  // Simulación de datos del producto (en producción vendría de una API)
  const products = {
    'poncho-tradicional': {
      id: 'poncho-tradicional',
      name: 'Poncho Andino Tradicional',
      category: 'Ponchos',
      price: 149,
      originalPrice: 189,
      description: 'Auténtico poncho andino tejido a mano por artesanos peruanos. Elaborado con lana de alpaca 100% natural, este poncho combina la tradición milenaria con diseños contemporáneos.',
      features: [
        'Lana de alpaca 100% natural',
        'Tejido a mano por artesanos locales',
        'Diseños tradicionales cusqueños',
        'Cuidado fácil - lavable a mano',
        'Producto de comercio justo'
      ],
      sizes: ['S', 'M', 'L', 'XL'],
      colors: ['Natural', 'Rojo', 'Azul'],
      icon: 'fas fa-tshirt',
      badge: 'Nuevo',
      badgeColor: 'var(--secondary)'
    },
    'pollera-cusqueña': {
      id: 'pollera-cusqueña',
      name: 'Pollera Cusqueña Bordada',
      category: 'Polleras',
      price: 125,
      description: 'Hermosa pollera tradicional con bordados a mano que representan la flora y fauna andina. Perfecta para ocasiones especiales o uso diario.',
      features: [
        'Bordados a mano',
        'Tela de alta calidad',
        'Diseños inspirados en la naturaleza andina',
        'Elaboración tradicional',
        'Apoya a comunidades artesanas'
      ],
      sizes: ['S', 'M', 'L', 'XL'],
      colors: ['Negro', 'Morado', 'Verde'],
      icon: 'fas fa-female',
      badge: 'Popular',
      badgeColor: 'var(--accent)'
    },
    'chaleco-alpaca': {
      id: 'chaleco-alpaca',
      name: 'Chaleco de Alpaca Premium',
      category: 'Chalecos',
      price: 199,
      description: 'Chaleco premium de alpaca baby, suave y cálido. Ideal para climas fríos, combina estilo y funcionalidad con diseños geométricos andinos.',
      features: [
        'Alpaca baby premium',
        'Extremadamente suave y cálido',
        'Diseños geométricos tradicionales',
        'Confección de alta calidad',
        'Hipoalergénico y transpirable'
      ],
      sizes: ['S', 'M', 'L', 'XL', 'XXL'],
      colors: ['Beige', 'Marrón', 'Gris'],
      icon: 'fas fa-vest'
    },
    'chal-tejido': {
      id: 'chal-tejido',
      name: 'Chal Tejido a Mano',
      category: 'Chales',
      price: 65,
      originalPrice: 85,
      description: 'Chal versátil y elegante tejido a mano. Perfecto para complementar cualquier outfit con un toque de cultura andina.',
      features: [
        'Tejido 100% a mano',
        'Mezcla de alpaca y lana',
        'Diseño versátil',
        'Acabados de calidad',
        'Colores naturales'
      ],
      sizes: ['Único'],
      colors: ['Natural', 'Terracota', 'Azul'],
      icon: 'fas fa-scarf',
      badge: 'Oferta',
      badgeColor: 'var(--secondary)'
    }
  };

  const product = products[id] || products['poncho-tradicional'];

  const handleAddToCart = () => {
    addToCart({
      id: product.id,
      name: product.name,
      price: product.price,
      size: selectedSize,
      quantity: quantity
    });
    toggleCartSidebar();
  };

  const increaseQuantity = () => setQuantity(quantity + 1);
  const decreaseQuantity = () => {
    if (quantity > 1) setQuantity(quantity - 1);
  };

  return (
    <div className="product-detail">
      <div className="container">
        <button className="back-button" onClick={() => navigate('/')}>
          <i className="fas fa-arrow-left"></i> Volver a la tienda
        </button>

        <div className="product-detail-content">
          <div className="product-detail-image">
            {product.badge && (
              <div
                className="product-badge"
                style={{ background: product.badgeColor }}
              >
                {product.badge}
              </div>
            )}
            <i className={product.icon}></i>
          </div>

          <div className="product-detail-info">
            <p className="product-category">{product.category}</p>
            <h1>{product.name}</h1>

            <div className="product-price-section">
              <span className="price-current">S/. {product.price}</span>
              {product.originalPrice && (
                <span className="price-original">S/. {product.originalPrice}</span>
              )}
            </div>

            <div className="product-description">
              <p>{product.description}</p>
            </div>

            <div className="product-features">
              <h3>Características:</h3>
              <ul>
                {product.features.map((feature, index) => (
                  <li key={index}>
                    <i className="fas fa-check"></i>
                    {feature}
                  </li>
                ))}
              </ul>
            </div>

            <div className="product-options">
              <div className="option-group">
                <label>Talla:</label>
                <div className="size-selector">
                  {product.sizes.map((size) => (
                    <button
                      key={size}
                      className={`size-btn ${selectedSize === size ? 'active' : ''}`}
                      onClick={() => setSelectedSize(size)}
                    >
                      {size}
                    </button>
                  ))}
                </div>
              </div>

              <div className="option-group">
                <label>Cantidad:</label>
                <div className="quantity-selector">
                  <button onClick={decreaseQuantity}>
                    <i className="fas fa-minus"></i>
                  </button>
                  <span>{quantity}</span>
                  <button onClick={increaseQuantity}>
                    <i className="fas fa-plus"></i>
                  </button>
                </div>
              </div>
            </div>

            <div className="product-actions-detail">
              <button className="btn btn-primary btn-large" onClick={handleAddToCart}>
                <i className="fas fa-cart-plus"></i>
                Agregar al Carrito - S/. {(product.price * quantity).toFixed(2)}
              </button>
            </div>

            <div className="product-shipping">
              <div className="shipping-item">
                <i className="fas fa-truck"></i>
                <span>Envío gratis en compras mayores a S/. 200</span>
              </div>
              <div className="shipping-item">
                <i className="fas fa-undo"></i>
                <span>Devoluciones gratuitas dentro de 30 días</span>
              </div>
              <div className="shipping-item">
                <i className="fas fa-shield-alt"></i>
                <span>Compra protegida y segura</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductDetail;
