import React from 'react';
import { useApp } from '../../context/AppContext';
import './Products.css';

function Products() {
  const { addToCart } = useApp();

  const products = [
    {
      id: 'poncho-tradicional',
      name: 'Poncho Andino Tradicional',
      category: 'Ponchos',
      price: 149,
      originalPrice: 189,
      badge: 'Nuevo',
      badgeColor: 'var(--secondary)',
      icon: 'fas fa-tshirt'
    },
    {
      id: 'pollera-cusqueña',
      name: 'Pollera Cusqueña Bordada',
      category: 'Polleras',
      price: 125,
      badge: 'Popular',
      badgeColor: 'var(--accent)',
      icon: 'fas fa-female'
    },
    {
      id: 'chaleco-alpaca',
      name: 'Chaleco de Alpaca Premium',
      category: 'Chalecos',
      price: 199,
      icon: 'fas fa-vest'
    },
    {
      id: 'chal-tejido',
      name: 'Chal Tejido a Mano',
      category: 'Chales',
      price: 65,
      originalPrice: 85,
      badge: 'Oferta',
      badgeColor: 'var(--secondary)',
      icon: 'fas fa-scarf'
    }
  ];

  const handleAddToCart = (product) => {
    addToCart({
      id: product.id,
      name: product.name,
      price: product.price
    });
    alert(`¡${product.name} agregado al carrito por S/. ${product.price}!`);
  };

  const handleQuickView = (product) => {
    alert(`Vista rápida de: ${product.name}`);
  };

  return (
    <section className="products" id="tienda">
      <div className="container">
        <h2 className="section-title">Productos Destacados</h2>
        <div className="product-grid">
          {products.map((product) => (
            <div key={product.id} className="product-card">
              <div className="product-image">
                {product.badge && (
                  <div 
                    className="product-badge" 
                    style={{ background: product.badgeColor }}
                  >
                    {product.badge}
                  </div>
                )}
                <i className={product.icon}></i>
              </div>
              <div className="product-info">
                <p className="product-category">{product.category}</p>
                <h3 className="product-name">{product.name}</h3>
                <div className="product-price">
                  <span className="price-current">S/. {product.price}</span>
                  {product.originalPrice && (
                    <span className="price-original">S/. {product.originalPrice}</span>
                  )}
                </div>
                <div className="product-actions">
                  <button 
                    className="btn btn-outline btn-sm"
                    onClick={() => handleQuickView(product)}
                  >
                    Vista Rápida
                  </button>
                  <button 
                    className="btn btn-primary btn-sm" 
                    onClick={() => handleAddToCart(product)}
                  >
                    <i className="fas fa-cart-plus"></i> Agregar
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default Products;
